import math


def trim_leading_0s_and_tailing_1s(s: str) -> str:
    while s and s[0] == '0':
        s = s[1:]
    while s and s[-1] == '1':
        s = s[:-1]
    return s


def flip_first(s: str) -> str:
    if s[0] == '0':
        s = '1' + s[1:]
    elif s[0] == '1':
        s = '0' + s[1:]
    return s


def flip_last(s: str) -> str:
    if s[-1] == '0':
        s = s[:-1] + '1'
    elif s[-1] == '1':
        s = s[:-1] + '0'
    return s


def min_flips_dynamic(s: str, flips: int, memo: dict) -> int:
    s = trim_leading_0s_and_tailing_1s(s)
    if not s:
        return flips
    if s in memo:
        return flips + memo[s]
    s1 = flip_first(s)
    s2 = flip_last(s)
    res1 = min_flips_dynamic(s1, flips + 1, memo)
    res2 = min_flips_dynamic(s2, flips + 1, memo)
    result = min(res1, res2)
    memo[s] = result - flips
    return result


def min_flips_mono_incr(s: str) -> int:
    return min_flips_dynamic(s, 0, {'10': 1})


print(min_flips_mono_incr("00110"))
print(min_flips_mono_incr("010110"))
print(min_flips_mono_incr("00011000"))
print(min_flips_mono_incr("001011"))
print(min_flips_mono_incr("111011100100100"))
